import Header from '../components/Header';
import imgFavorite from '../images/blackHeartIcon.svg';
import imgShare from '../images/shareIcon.svg';

function FavoriteRecipes({ index,
  type,
  name,
  imag,
  category,
  alcool,
  nationality }) {
  const checkType = type === 'meals' ? nationality : alcool;
  return (
    <div>
      <Header search="false" title="Favorite Recipes" />

      <button
        name="all"
        type="button"
        data-testid="filter-by-all-btn"
      >
        All
      </button>

      <button
        name="meals"
        type="button"
        data-testid="filter-by-meal-btn"
      >
        Filter Meals
      </button>

      <button
        name="drinks"
        type="button"
        data-testid="filter-by-drink-btn"
      >
        Filter Drinks
      </button>

      <img
        data-testid={ `${index}-horizontal-image` }
        src={ imag }
        alt={ name }
      />

      <h2
        data-testid={ `${index}-horizontal-top-text` }
      >
        {`${checkType} - ${category}`}
      </h2>

      <h1 data-testid={ `${index}-horizontal-name` }> </h1>

      <img
        alt="share"
        src={ imgShare }
        data-testid={ `${index}-horizontal-share-btn` }
      />

      <img
        alt="BotÃ£o de Favorito"
        src={ imgFavorite }
        data-testid={ `${index}-horizontal-favorite-btn` }
      />
    </div>
  );
}

FavoriteRecipes.propTypes = {
  index: PropTypes.number,
  id: PropTypes.number,
  img: PropTypes.string,
  name: PropTypes.string,
}.isRequired;

export default FavoriteRecipes;
