import React, { useEffect, useState } from 'react';
import copy from 'clipboard-copy';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import favoriteIcon from '../images/blackHeartIcon.svg';
import shareIcon from '../images/shareIcon.svg';

function FavoriteRecipes() {
  const [favorite, setFilter] = useState('');
  const [favoriteRecipes, setFavoriteRecipes] = useState([]);
  const data = JSON.parse(localStorage.getItem('favoriteRecipes'));

  useEffect(() => {
    if (data);
    setFavoriteRecipes(data);
  }, []);

  useEffect(() => {
  }, [favoriteRecipes]);

  const remove = (index) => {
    data.splice(index, [1]);
    localStorage
      .setItem('favoriteRecipes', JSON.stringify(data));
    setFavoriteRecipes(data);
  };

  return (
    <span>
      <Header search="false" title="Favorite Recipes" />

      <button
        onClick={ () => setFilter('') }
        name="all"
        type="button"
        data-testid="filter-by-all-btn"
      >
        All
      </button>

      <button
        onClick={ () => setFilter('meal') }
        name="meals"
        type="button"
        data-testid="filter-by-meal-btn"
      >
        Filter Meals
      </button>

      <button
        onClick={ () => setFilter('drink') }
        name="drinks"
        type="button"
        data-testid="filter-by-drink-btn"
      >
        Filter Drinks
      </button>

      {favoriteRecipes.length !== 0
      && favoriteRecipes
        .filter((element) => (!favorite ? element : element.type === favorite))
        .map((favorite, index) => (
          <span key={ favorite.id }>
            <Link to={ `/${favorite.type}s/${favorite.id}` }>
              <h2 data-testid={ `${index}-horizontal-name` }>{favorite.name}</h2>
              <img
                data-testid={ `${index}-horizontal-image` }
                src={ favorite.image }
                alt={ favorite.name }
              />
            </Link>
            <button
              type="button"
              data-testid={ `${index}-sharebutton` }
              onClick={ ({ target }) => {
                target.innerHTML = 'Link copied!';
                copy(`http://localhost:3000/${favorite.type}s/${favorite.id}`);
              } }
            >
              <img
                alt="Botão de Compartilhar"
                src={ shareIcon }
                data-testid={ `${index}-horizontal-share-btn` }
              />
            </button>

            <button
              type="button"
              onClick={ () => remove(index) }
            >
              <img
                alt="Botão de Favorito"
                src={ favoriteIcon }
                data-testid={ `${index}-horizontal-favorite-btn` }
              />
            </button>
            <p data-testid={ `${index}-horizontal-top-text` }>
              {favorite.type === 'drink'
                ? favorite.alcoholicOrNot
                : `${favorite.nationality} - ${favorite.category}`}
            </p>
          </span>
        ))}
    </span>
  );
}
export default FavoriteRecipes;
