import React, { useEffect, useState } from 'react';
import copy from 'clipboard-copy';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import favoriteIcon from '../images/blackHeartIcon.svg';
import shareIcon from '../images/shareIcon.svg';

function FavoriteRecipes() {
  const [filter, setFilter] = useState('');
  const [favoriteRecipes, setFavoriteRecipes] = useState([]);
  const data = JSON.parse(localStorage.getItem('favoriteRecipes'));

  useEffect(() => {
    if (data);
    setFavoriteRecipes(data);
  }, []);

  useEffect(() => {
  }, [favoriteRecipes]);

  const remove = (i) => {
    data.splice(i, [1]);
    localStorage
      .setItem('favoriteRecipes', JSON.stringify(data));
    setFavoriteRecipes(data);
  };

  return (
    <div>
      <Header search="false" title="Favorite Recipes" />

      <button
        onClick={ () => setFilter('') }
        name="all"
        type="button"
        data-testid="filter-by-all-btn"
      >
        All
      </button>

      <button
        onClick={ () => setFilter('meal') }
        name="meals"
        type="button"
        data-testid="filter-by-meal-btn"
      >
        Filter Meals
      </button>

      <button
        name="drinks"
        type="button"
        data-testid="filter-by-drink-btn"
        onClick={ () => setFilter('drink') }
      >
        Filter Drinks
      </button>

      {favoriteRecipes.length !== 0
      && favoriteRecipes.filter((e) => (!filter ? e : e.type === filter))
        .map((recipe, i) => (
          <div id="mainAoQuadraxion" key={ recipe.id }>
            <Link to={ `/${recipe.type}s/${recipe.id}` }>
              <h2 data-testid={ `${i}-horizontal-name` }>{recipe.name}</h2>
              <img
                data-testid={ `${i}-horizontal-image` }
                src={ recipe.image }
                alt={ recipe.name }
              />
            </Link>
            <button
              type="button"
              data-testid={ `${i}-sharebutton` }
              onClick={ ({ target }) => {
                target.innerHTML = 'Link copied!';
                copy(`http://localhost:3000/${recipe.type}s/${recipe.id}`);
              } }
            >
              <img
                alt="Botão de Compartilhar"
                src={ shareIcon }
                data-testid={ `${i}-horizontal-share-btn` }
              />
            </button>

            <button
              type="button"
              onClick={ () => remove(i) }
            >
              <img
                alt="Botão de Favorito"
                src={ favoriteIcon }
                data-testid={ `${i}-horizontal-favorite-btn` }
              />
            </button>
            <p data-testid={ `${i}-horizontal-top-text` }>
              {recipe.type === 'drink'
                ? recipe.alcoholicOrNot
                : `${recipe.nationality} - ${recipe.category}`}
            </p>
          </div>
        ))}
    </div>
  );
}
export default FavoriteRecipes;
