import PropTypes from 'prop-types';
import Header from '../components/Header';
import imgFavorite from '../images/blackHeartIcon.svg';
import imgShare from '../images/shareIcon.svg';

function FavoriteRecipes() {
  const [favoriteRecipes, setFavoriteRecipes] = useState([]);
  const localStorageFavorite = JSON.parse(localStorage.getItem('favoriteRecipes'));
  const favoriteType = type === 'drink' ? alcoholicOrNot : nationality;

  useEffect(() => {
    if (localStorageFavorite);
    setFavoriteRecipes(localStorageFavorite);
  }, []);

  return (
    <div>
      <Header search="false" title="Favorite Recipes" />

      <button
        name="all"
        type="button"
        data-testid="filter-by-all-btn"
      >
        All
      </button>

      <button
        name="meals"
        type="button"
        data-testid="filter-by-meal-btn"
      >
        Filter Meals
      </button>

      <button
        name="drinks"
        type="button"
        data-testid="filter-by-drink-btn"
      >
        Filter Drinks
      </button>

      { favoriteRecipes.length !== 0
      && favoriteRecipes
        .filter((element) => (!filter ? element : element.type === filter))
        .map((favorite, index) => (
          <span key={ favorite.id }>

            <img
              data-testid={ `${index}-horizontal-image` }
              src={ favorite.image }
              alt={ favorite.name }
            />

            <h1 data-testid={ `${index}-horizontal-name` }>{ favorite.name }</h1>

            <p
              data-testid={ `${index}-horizontal-top-text` }
            >
              {`${favoriteType} - ${favorite.category}`}

            </p>

            <button
              type="button"
            >
              SHARE
              <img
                alt="share"
                src={ imgShare }
                data-testid={ `${index}-horizontal-share-btn` }
              />
            </button>

            <button
              type="button"
            >
              FAVORITE
              <img
                alt="favorito"
                src={ imgFavorite }
                data-testid={ `${index}-horizontal-favorite-btn` }
              />
            </button>
          </span>
        ))}
    </div>
  );
}

FavoriteRecipes.propTypes = {
  img: PropTypes.string,
  name: PropTypes.string,
}.isRequired;

export default FavoriteRecipes;
